import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


function TaskDisplay({ tasks, deleteTask }) {
  return (
    <div>
      <h3>Task List</h3>
      {tasks.length === 0 ? (
        <p>No tasks available</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th><h4>Task</h4>&nbsp;&nbsp;&nbsp;&nbsp;</th>
              <th><h3>Status</h3>&nbsp;&nbsp;</th>
              <th><h3>Duration</h3>&nbsp;&nbsp;&nbsp;&nbsp;</th>
              <th><h3>Delete the task</h3>&nbsp;&nbsp;</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.name} | &nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>{task.status} | &nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>{task.duration} hour(s) | &nbsp;&nbsp;&nbsp;&nbsp;</td>
                <td>
                  <button onClick={() => deleteTask(task.id)} class="btn btn-secondary">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TaskDisplay;
