import React from 'react';

function TaskDisplay({ tasks, deleteTask }) {
  return (
    <div>
      <h3>Task List</h3>
      {tasks.length === 0 ? (
        <p>No tasks available</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th><h3>Task</h3></th>
              <th>Status</th>
              <th>Duration</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.name} | &nbsp;&nbsp;</td>
                <td>{task.status} |</td>
                <td>{task.duration} hour(s) |</td>
                <td>
                  <button onClick={() => deleteTask(task.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TaskDisplay;
